import React, { useState, useEffect } from "react";
import { Form, Button, Row, Col } from "react-bootstrap";

import { Link } from "react-router-dom";
import Loading from "../../components/Loading";
import ErrorMessage from "../../components/ErrorMessage";
import axios from 'axios';
import MainScreen from "../../components/MainScreen";
import "./RegisterScreen.css";


function RegisterScreen() {

  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
  const [pic, setPic] = useState(
    "https://icon-library.com/images/anonymous-avatar-icon/anonymous-avatar-icon-25.jpg"
  );
  const [password, setPassword] = useState("");
  const [confirmpassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState(null);
  const [picMessage, setPicMessage] = useState(null);
  const [error, setError]= useState(false);
  const [loading, setLoading] = useState(false);



  const postDetails = (pics) => {
    if (
      pics ===
      "https://icon-library.com/images/anonymous-avatar-icon/anonymous-avatar-icon-25.jpg"
    ) {
      return setPicMessage("Please Select an Image");
    }
    setPicMessage(null);
    if (pics.type === "image/jpeg" || pics.type === "image/png") {
      const data = new FormData();
      data.append("file", pics);
      data.append("upload_preset", "quizApp");
      data.append("cloud_name", "dub9dgblf");
      fetch("https://api.cloudinary.com/v1_1/dub9dgblf/image/upload", {
        method: "post",
        body: data,
      })
        .then((res) => res.json())
        .then((data) => {
          setPic(data.url.toString());
        })
        .catch((err) => {
          console.log(err);
        });
    } else {
      return setPicMessage("Please Select an Image");
    }
  };


  const submitHandler = async (e) => {
    e.preventDefault();
    if (password !== confirmpassword) {
      setMessage("Passwords do not match");
    } else {
      setMessage(null)
      setLoading(true);
    }try{
      const config = {
        
        headers:{

           "Content-type":"application/json"
       }
       }

       const {data} = await axios.post("http://localhost:5000/api/users",
       {
        name,
       email,
       password,
       pic

       },config);

       console.log(data);
       localStorage.setItem("userInfo", JSON.stringify(data));

   setLoading(false)



    }catch(error){
      setError(error.response.data.message);
      setLoading(false)

    }
   console.log(email)
  };



  return (
   
    <MainScreen title="REGISTER">
    <div className="registerContainer">
      <div className="register-left">
        <h2>Welcome Back</h2>
        <Button className="signin-btn" as={Link} to="/login">
          Sign in
        </Button>
      </div>
      <div className="register-right">
        <h2>Create Account</h2>
        {error && <ErrorMessage variant="danger">{error}</ErrorMessage>}
        {message && <ErrorMessage variant="danger">{message}</ErrorMessage>}
        {loading && <Loading />}
        <Form onSubmit={submitHandler}>
          <Form.Group controlId="name">
            <Form.Label>First Name</Form.Label>
            <Form.Control
              type="text"
              value={name}
              placeholder="Enter first name"
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="lastname">
            <Form.Label>Last Name</Form.Label>
            <Form.Control
              type="text"
              value={name}
              placeholder="Enter last name"
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control
              type="email"
              value={email}
              placeholder="Enter email"
              onChange={(e) => setEmail(e.target.value)}
            />
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              placeholder="Password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>

          <Form.Group controlId="confirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              value={confirmpassword}
              placeholder="Confirm Password"
              onChange={(e) => setConfirmPassword(e.target.value)}
            />
          </Form.Group>
          {picMessage && (
            <ErrorMessage variant="danger">{picMessage}</ErrorMessage>
          )}
          <Form.Group controlId="pic">
            <Form.Label>Profile Picture</Form.Label>
            <div className="profilePicContainer">
              <img src={pic} alt="Profile" className="profilePic" />
              <Form.Control
                type="file"
                label="Upload Profile Picture"
                onChange={(e) => postDetails(e.target.files[0])}
                accept="image/*"
              />
            </div>
          </Form.Group>

          <Button className="signup-btn" type="submit">
            Sign Up
          </Button>
        </Form>
        <Row className="py-3">
          <Col>
            Have an Account? <Link to="/login">Login</Link>
          </Col>
        </Row>
      </div>
    </div>
  </MainScreen>
 
  )
}

export default RegisterScreen
