import React, { useEffect } from "react";
import { Button, Container, Row,Col, } from "react-bootstrap";
import quizImage from '/quizz.png"';

import "./LandingStyles.css";

function LandingPage() {


  return (
    <div className="main">
    <Container>
      <Row>
        <Col md={6} className="intro-text">
          <div>
            <h1 className="title">Welcome to Quizzy App</h1>
            <p className="subtitle">One safe place for all your quizzes.</p>
            <div className="buttonContainer">
              <Button size="lg" className="landingbutton">Login</Button>
              <Button variant="outline-primary" size="lg" className="landingbutton">Sign Up</Button>
            </div>
          </div>
        </Col>
        <Col md={6}>
          <img src={quizImage} alt="Quiz Image" className="img-fluid" />
        </Col>
      </Row>
    </Container>
  </div>
  );
}

export default LandingPage;
